orm').on('submit', function (e) {			e.preventDefault();			data = $(this).serialize();			url  = $(this).attr('action');			$.ajax({			 url:   url,			 type:  'POST',			 data:  data,			 success: function (data) {				 console.log(data);				 if ($.isEmptyObject(data.error))				 {				  $('.print-success-msg').find('ul').empty().append("<li>" +  data.success + "</li>");				  $('.print-success-msg').css('display', 'block');			    $('.print-error-msg').css('display', 'none');				 } else {				  printMessageErrors(data.error);				 }			 }			});		});	 		function printMessageErrors(msg) {			$('.print-error-msg').find('ul').empty();			$('.print-error-msg').css('display', 'block');			$('.print-success-msg').css('display', 'none');			$.each(msg, function (key, value) {			 $('.print-error-msg').find('ul').append("<li>" +  value + "</li>");			});		} </script>@endsection