@extends('layouts.master')@section('content')	<div class="col-md-12">		<div class="card">			<div class="card-header card-header-primary">				<div>Pagination</div>			</div>			<div class="card-body">				@include('ajax.studentPage')			</div>		</div>	</div>@endsection@section('scripts')	<script type="text/javascript">		$(document).on('click', '.pagination a', function (e) {			e.preventDefault();			var page = $(this).attr('href').split('page=')[1];			readPage(page);		});		function readPage(page) {			$.ajax({				url: '/students/page/ajax?page='+page			}).done(function (data) {				$('.card-body').html(data);				location.hash=page;			})		}	</script>@endsection